import { createStore } from "vuex";

export default createStore({
  // データの状態
  state: {
    count: 0,
    memos: [],
  },
  // データの取得
  getters: {
    getCount: (state) => {
      return state.memos.length;
    },
    getMemos: (state) => {
      return state.memos;
    },
    getSingleMemo: (state) => {
      return state.memos.find((memo) => memo.id == id);
    },
  },
  // データの更新
  mutations: {
    saveMutation: (state, newMemo) => {
      newMemo.id = ++state.count;
      state.memos.unshift(newMemo);
    },
  },
});
